CREATE DATABASE IF NOT EXISTS APP_PROJECT_DATABASE;
USE APP_PROJECT_DATABASE;

CREATE TABLE IF NOT EXISTS Student_Records (
  Student_ID INT AUTO_INCREMENT PRIMARY KEY,
  Name VARCHAR(100) NOT NULL,
  SRM_Email_ID VARCHAR(100) UNIQUE NOT NULL,
  Password VARCHAR(100) NOT NULL,
  UHV_II_Marks INT,
  UHV_II_Attendance DECIMAL(5,2),
  COA_Marks INT,
  COA_Attendance DECIMAL(5,2),
  ADV_PROG_Marks INT,
  ADV_PROG_Attendance DECIMAL(5,2),
  DSA_Marks INT,
  DSA_Attendance DECIMAL(5,2),
  DSA_LAB_Marks INT,
  DSA_LAB_Attendance DECIMAL(5,2),
  PROF_ETH_Marks INT,
  PROF_ETH_Attendance DECIMAL(5,2),
  OODP_Marks INT,
  OODP_Attendance DECIMAL(5,2),
  RPI_Marks INT,
  RPI_Attendance DECIMAL(5,2),
  CHECK (UHV_II_Marks BETWEEN 0 AND 60)
);

